import React from "react";
import logo from "./logo.svg";
import "./App.css";
import { normalize } from "./hooks/normalize";
import { QueryClient, QueryClientProvider, useQuery } from "react-query";
import { useWeatherInfo } from "./hooks/useWeatherInfo";

const queryClient = new QueryClient();

function App() {
  const [userLocation, setUserLocation] = React.useState<
    { latitude: number; longitude: number } | string
  >("Cologne");

  const { weather } = useWeatherInfo(userLocation);

  // define the function that finds the users geolocation
  const getUserLocation = () => {
    // if geolocation is supported by the users browser
    if (navigator.geolocation) {
      // get the current users location
      navigator.geolocation.getCurrentPosition(
        (position) => {
          // save the geolocation coordinates in two variables
          const { latitude, longitude } = position.coords;
          // update the value of userlocation variable
          setUserLocation({ latitude, longitude });
        },
        // if there was an error getting the users location
        (error) => {
          console.error("Error getting user location:", error);
        }
      );
    }
    // if geolocation is not supported by the users browser
    else {
      console.error("Geolocation is not supported by this browser.");
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <button onClick={getUserLocation}>Get User Location</button>
        {userLocation ? (
          <div>
            <h2>User Location</h2>
            <p>Latitude: {userLocation.latitude || "0"}</p>
            <p>Longitude: {userLocation.longitude || "0"}</p>
          </div>
        ) : null}
        {weather ? <p>{weather.name}</p> : null}
        {weather?.extras?.map((item) => (
          <p key={item}>{item}</p>
        ))}
        {weather?.top ? <p>{weather?.top}</p> : undefined}
        {weather?.legs ? <p>{weather?.legs}</p> : undefined}
        {weather?.bottom ? <p>{weather?.bottom}</p> : undefined}
      </header>
    </div>
  );
}

export default App;
