import React from "react";
import logo from "./logo.svg";
import "./App.css";
import { useWeatherInfo } from "./hooks/useWeatherInfo";
import Button from "@mui/joy/Button";
import Input from "@mui/joy/Input";
import Stack from "@mui/joy/Stack";
import LocationOn from "@mui/icons-material/LocationOn";
import { useLocation } from "./hooks/useLocation";
import Typography from "@mui/joy/Typography";

const colors = {
  text: "white",
};

function App() {
  const [searchInput, setSearchInput] = React.useState("");

  const { userLocation, getUserLocation } = useLocation();
  const [location, setLocation] = React.useState(userLocation);
  const { weather } = useWeatherInfo(location);

  const getLocation = () => {
    getUserLocation();
    setLocation(userLocation);
  };

  return (
    <div className="App">
      <header className="App-header">
        {/* <img src={logo} className="App-logo" alt="logo" /> */}
        {/*         {userLocation ? (
          <div>
            <h2>User Location</h2>
            <p>Latitude: {userLocation.latitude || "0"}</p>
            <p>Longitude: {userLocation.longitude || "0"}</p>
          </div>
        ) : null} */}
        <Stack spacing={4}>
          <Typography level="h2" color="neutral">
            what should i wear today
          </Typography>
          <form
            onSubmit={(event) => {
              event.preventDefault();
              setLocation(searchInput);
            }}
          >
            <Input
              placeholder="Cologne"
              onChange={(event) =>
                event.currentTarget.value
                  ? setSearchInput(event.currentTarget.value)
                  : undefined
              }
              startDecorator={
                <Button
                  onClick={getLocation}
                  variant="soft"
                  color="neutral"
                  startDecorator={<LocationOn />}
                >
                  Locate
                </Button>
              }
              sx={{ width: 300 }}
            />
          </form>
          {weather ? (
            <Typography level="h2" color="neutral">
              {weather.name}
            </Typography>
          ) : null}
          {weather?.extras?.map((item) => (
            <Typography level="h2" color="neutral" key={item}>
              {item}
            </Typography>
          ))}
          {weather?.top ? (
            <Typography level="h2" color="neutral">
              {weather?.top}
            </Typography>
          ) : undefined}
          {weather?.legs ? (
            <Typography level="h2" color="neutral">
              {weather?.legs}
            </Typography>
          ) : undefined}
          {weather?.bottom ? (
            <Typography level="h2" color="neutral">
              {weather?.bottom}
            </Typography>
          ) : undefined}
        </Stack>
      </header>
    </div>
  );
}

export default App;
