        {data?.extras?.map((item) => (
          <p key={item}>{item}</p>
        ))}
        {data?.top ? <p>{data?.top}</p> : undefined}
        {data?.legs ? <p>{data?.legs}</p> : undefined}
        {data?.bottom ? <p>{data?.bottom}</p> : undefined}
      </header>
    </div>
  );
}

export default App;
