import React from "react";
import logo from "./logo.svg";
import "./App.css";
import { useWeatherInfo } from "./hooks/useWeatherInfo";
import Button from "@mui/joy/Button";
import Input from "@mui/joy/Input";
import Stack from "@mui/joy/Stack";
import LocationOn from "@mui/icons-material/LocationOn";
import { useLocation } from "./hooks/useLocation";
import Typography from "@mui/joy/Typography";

function App() {
  const [searchInput, setSearchInput] = React.useState("");

  const { userLocation, getUserLocation } = useLocation();
  const [location, setLocation] = React.useState(userLocation);
  const { weather } = useWeatherInfo(location);

  return (
    <div className="App">
      <header className="App-header">
        {/* <img src={logo} className="App-logo" alt="logo" /> */}
        {/*         {userLocation ? (
          <div>
            <h2>User Location</h2>
            <p>Latitude: {userLocation.latitude || "0"}</p>
            <p>Longitude: {userLocation.longitude || "0"}</p>
          </div>
        ) : null} */}
        <Typography level="h2">what should i wear today</Typography>
        <form
          onSubmit={(event) => {
            event.preventDefault();
            setLocation(searchInput);
          }}
        >
          <Input
            placeholder="Cologne"
            onChange={(event) =>
              event.currentTarget.value
                ? setSearchInput(event.currentTarget.value)
                : undefined
            }
            startDecorator={
              <Button
                onClick={getUserLocation}
                variant="soft"
                color="neutral"
                startDecorator={<LocationOn />}
              >
                Locate
              </Button>
            }
            sx={{ width: 300 }}
          />
        </form>
        <Stack>
          {weather ? <p>{weather.name}</p> : null}
          {weather?.extras?.map((item) => (
            <p key={item}>{item}</p>
          ))}
          {weather?.top ? <p>{weather?.top}</p> : undefined}
          {weather?.legs ? <p>{weather?.legs}</p> : undefined}
          {weather?.bottom ? <p>{weather?.bottom}</p> : undefined}
        </Stack>
      </header>
    </div>
  );
}

export default App;
